import Card from '@/components/Card';
export default {
  data() {
    return {
      commodityList: []
    };
  },
  components: {
    Card
  },
  created() {},
  methods: {
    loadData() {
      this.$axios.get('/commodity/getallsell').then(res => {
        if (res.data.code == 200) {
          this.commodityList = res.data.result;
        } else {
          this.$alert(res.data.message, '提示', {
            confirmButtonText: '确定'
          });
        }
      });
    }
  }
};
