import Vue from 'vue'
import Router from 'vue-router'

import Login from '@/views/Login'
import Register from '@/views/Register'
import Layout from '@/views/Layout'
import MyInfo from '@/views/MyInfo'

//运营方——————————————————————————————
import Operator from '@/views/Operator'
//商品管理
import Commodity from '@/views/Operator/Commodity'
import CommodityAdd from '@/views/Operator/Commodity/Add'
//类别管理
import Type from '@/views/Operator/Type'
//订单管理
import Orders from '@/views/Operator/Orders'
//销售统计
import Analysis from '@/views/Operator/Analysis'
//广告维护
import Advertisement from '@/views/Operator/Advertisement'

//买家————————————————————————————————
import Consumer from '@/views/Consumer'
//商城首页
import Homepage from '@/views/Consumer/Homepage'
//购物车
import Cart from '@/views/Consumer/Cart'
//我的订单
import MyOrders from '@/views/Consumer/Orders'

Vue.use(Router)

const router = new Router({
  
  mode: 'history',
  
  routes: [
    //登录
    { 
      path: '/login', 
      name: 'login',
      component: Login,
    },

    //注册
    { 
      path: '/register', 
      name: 'register',
      component: Register,
    },

    //其他界面
    { 
      path: '/',
      component: Layout,
      meta:{
        requireAuth:true
      },
      children:[
        //当前用户详情——————————————
        {
          path:'/myinfo',
          name:'myinfo',
          component:MyInfo,
        },

        //运营方————————————————————
        {
          path:'/operator',
          name:'operator',
          component:Operator,
          
          children:[
            //商品管理
            {
              path:'commodity',
              name:'commodity',
              component:Commodity,

              children:[
                {
                  path:'add',
                  name:'commodityadd',
                  component:CommodityAdd,
                }
              ]
            },

            //类别管理
            {
              path:'type',
              name:'type',
              component:Type
            },

            //订单管理
            {
              path:'orders',
              name:'orders',
              component:Orders
            },

            //销售统计
            {
              path:'analysis',
              name:'analysis',
              component:Analysis
            },

            //广告维护
            {
              path:'advertisement',
              name:'advertisement',
              component:Advertisement
            },
          ]
        },

        //买家————————————————————
        {
          path:'/consumer',
          name:'consumer',
          component:Operator,
          
          children:[
            //商品管理
            {
              path:'commodity',
              name:'commodity',
              component:Commodity,

              children:[
                {
                  path:'add',
                  name:'commodityadd',
                  component:CommodityAdd,
                }
              ]
            },

            //类别管理
            {
              path:'type',
              name:'type',
              component:Type
            },

            //订单管理
            {
              path:'orders',
              name:'orders',
              component:Orders
            },

            //销售统计
            {
              path:'analysis',
              name:'analysis',
              component:Analysis
            },

            //广告维护
            {
              path:'advertisement',
              name:'advertisement',
              component:Advertisement
            },
          ]
        },
      ]
    },
  ]
})

export default router
