            state: '正常',
            registertime: new Date(),
            lastlogin: new Date(),
            character: this.ruleForm.character
          }).then(res => {
            if (res.data.code == 200) {
              this.$message({
                type: 'success',
                message: "注册成功！"
              });
              this.$axios.get('/login/checklogin', {
