                  this.$store.commit('login', {
                    user: res.data.result,
                    isLogin: true
                  });
                  this.$router.replace('/');
                } else {
                  /*登陆失败*/
                  this.$alert(res.data.message, '提示', {
                    confirmButtonText: '确定'
                  });
                }
              });
            } else {
              this.$message({
                type: 'error',
                message: res.data.message
              });
            }
          });
        } else {
          this.$message({
            type: 'error',
            message: "请检查输入信息！"
          });
        }
      });
    },
