import Vue from 'vue'
import Router from 'vue-router'

import Login from '@/views/Login'
import Register from '@/views/Register'
import Layout from '@/views/Layout'
import MyInfo from '@/views/MyInfo'

//运营方——————————————————————————————
import Operator from '@/views/Operator'
//商品管理
import Commodity from '@/views/Operator/Commodity'
import CommodityAdd from '@/views/Operator/Commodity/Add'
import CommodityList from '@/views/Operator/Commodity/List'
import CommodityUpdate from '@/views/Operator/Commodity/Update'
import CommodityDetails from '@/views/Operator/Commodity/Details'
//类别管理
import Type from '@/views/Operator/Type'
import TypeList from '@/views/Operator/Type/List'
import TypeUpdate from '@/views/Operator/Type/Update'
import TypeAdd from '@/views/Operator/Type/Add'
//订单管理
import Orders from '@/views/Operator/Orders'
import OrdersList from '@/views/Operator/Orders/List'
import OrdersDetails from '@/views/Operator/Orders/Details'
//销售统计
import Analysis from '@/views/Operator/Analysis'
import AnalysisList from '@/views/Operator/Analysis/List'
//广告维护
import Advertisement from '@/views/Operator/Advertisement'
import AdvertisementList from '@/views/Operator/Advertisement/List'
import AdvertisementAdd from '@/views/Operator/Advertisement/Add'
import AdvertisementUpdate from '@/views/Operator/Advertisement/Update'

//买家————————————————————————————————
import Consumer from '@/views/Consumer'
//商城首页
import Homepage from '@/views/Consumer/Homepage'
import HomepageList from '@/views/Consumer/Homepage/List'
//购物车
import Cart from '@/views/Consumer/Cart'
import CartList from '@/views/Consumer/Cart/List'
import CartOrder from '@/views/Consumer/Cart/Order'
//我的订单
import MyOrders from '@/views/Consumer/Orders'
import MyOrdersList from '@/views/Consumer/Orders/List'

Vue.use(Router)

const router = new Router({
  
  mode: 'history',
  
  routes: [

    //登录
    { 
      path: '/login', 
      name: 'login',
      component: Login,
    },

    //注册
    { 
      path: '/register', 
      name: 'register',
      component: Register,
    },

    //其他界面
    { 
      path: '/',
      component: Layout,
      meta:{
        requireAuth:true
      },
      children:[
        //当前用户详情——————————————
        {
          path:'/myinfo',
          name:'myinfo',
          component:MyInfo,
        },

        //运营方————————————————————
        {
          path:'/operator',
          name:'operator',
          component:Operator,
          
          children:[
            //商品管理
            {
              path:'commodity',
              name:'commodity',
              component:Commodity,

              children:[
                {
                  path:'add',
                  name:'commodityadd',
                  component:CommodityAdd,
                },
                {
                  path:'list',
                  name:'commoditylist',
                  component:CommodityList,
                },
                {
                  path:'update',
                  name:'commodityupdate',
                  component:CommodityUpdate,
                },
                {
                  path:'details',
                  name:'commoditydetails',
                  component:CommodityDetails,
                },
              ]
            },

            //类别管理
            {
              path:'type',
              name:'type',
              component:Type,

              children:[
                {
                  path:'list',
                  name:'typelist',
                  component:TypeList
                },
                {
                  path:'update',
                  name:'typeupdate',
                  component:TypeUpdate
                },
                {
                  path:'add',
                  name:'typeadd',
                  component:TypeAdd
                }
              ]
            },

            //订单管理
            {
              path:'orders',
              name:'orders',
              component:Orders,

              children:[
                {
                  path:'list',
                  name:'orderslist',
                  component:OrdersList
                },
                {
                  path:'details',
                  name:'ordersdetails',
                  component:OrdersDetails
                },
              ]
            },

            //销售统计
            {
              path:'analysis',
              name:'analysis',
              component:Analysis,
              children:[
                {
                  path:'list',
                  name:'analysislist',
                  component:AnalysisList
                },
              ]
            },

            //广告维护
            {
              path:'advertisement',
              name:'advertisement',
              component:Advertisement,
              children:[
                {
                  path:'list',
                  name:'advertisementlist',
                  component:AdvertisementList
                },
                {
                  path:'add',
                  name:'advertisementadd',
                  component:AdvertisementAdd
                },
                {
                  path:'add',
                  name:'advertisementadd',
                  component:AdvertisementAdd
                },
              ]
            },
          ]
        },

        //买家————————————————————
        {
          path:'/consumer',
          name:'consumer',
          component:Consumer,
          
          children:[
            //商城首页
            {
              path:'homepage',
              name:'homepage',
              component:Homepage,

              children:[
                {
                  path:'list',
                  name:'homepagelist',
                  component:HomepageList
                },
              ]
            },

            //购物车
            {
              path:'cart',
              name:'cart',
              component:Cart,

              children:[
                {
                  path:'list',
                  name:'cartlist',
                  component:CartList
                },
                {
                  path:'order',
                  name:'cartorder',
                  component:CartOrder
                },
              ]
            },

            //我的订单
            {
              path:'myorders',
              name:'myorders',
              component:MyOrders,

              children:[
                {
                  path:'list',
                  name:'myorderslist',
                  component:MyOrdersList
                },
              ]
            },
            
          ]
        },
      ]
    },
  ]
})

export default router
