import Vue from 'vue'
import Vuex from 'vuex'
import dateformat from '@/utils/dateformat'

Vue.use(Vuex)

const store = new Vuex.Store({
  state: {
    user: null,
    isLogin: false,
    type:null,
  },
  mutations: {
    login(state, payload){
      state.user=payload.user
      state.user.registertime=dateformat.dateFormat(state.user.registertime)
      state.user.lastlogin=dateformat.dateFormat(state.user.lastlogin)
      state.isLogin=payload.isLogin
      state.type=payload.user.character
    },
    logout(state){
      state.user=null,
      state.isLogin=false,
      state.type=null
    }
  }
})

export default store
