import Vue from 'vue'
import Router from 'vue-router'

import Login from '@/views/Login'
import Register from '@/views/Register'
import Layout from '@/views/Layout'
import MyInfo from '@/views/MyInfo'

Vue.use(Router)

const router = new Router({
  
  mode: 'history',
  
  routes: [
    //登录
    { 
      path: '/login', 
      name: 'login',
      component: Login,
    },

    //注册
    { 
      path: '/register', 
      name: 'register',
      component: Register,
    },

    //其他界面
    { 
      path: '/',
      component: Layout,
      meta:{
        requireAuth:true
      },
      children:[
        //当前用户详情
        {
          path:'/myinfo',
          name:'myinfo',
          component:MyInfo,
        },
      ]
    },
  ]
})

export default router
