import Vue from 'vue'
import Router from 'vue-router'

import Login from '@/views/Login'
import Register from '@/views/Register'
import Layout from '@/views/Layout'
import MyInfo from '@/views/MyInfo'

//运营方
import Operator from '@/views/Operator'
//商品管理
import Commodity from '@/views/Operator/Commodity'

//类别管理
import Type from '@/views/Operator/Type'

Vue.use(Router)

const router = new Router({
  
  mode: 'history',
  
  routes: [
    //登录
    { 
      path: '/login', 
      name: 'login',
      component: Login,
    },

    //注册
    { 
      path: '/register', 
      name: 'register',
      component: Register,
    },

    //其他界面
    { 
      path: '/',
      component: Layout,
      meta:{
        requireAuth:true
      },
      children:[
        //当前用户详情——————————————
        {
          path:'/myinfo',
          name:'myinfo',
          component:MyInfo,
        },

        //运营方————————————————————
        //商品管理
        {
          path:'/operator',
          name:'operator',
          component:Operator,
          
          children:[
            //商品管理
            {
              path:'commodity',
              name:'commodity',
              component:Commodity
            },

            //类别管理
            {
              path:'type',
              name:'type',
              component:Type
            },
          ]
        },
      ]
    },
  ]
})

export default router
