import Vue from 'vue'
import Router from 'vue-router'

import Login from '@/views/Login'
import Layout from '@/views/Layout'

Vue.use(Router)

const router = new Router({
  
  mode: 'history',
  
  routes: [
    //登录
    { 
      path: '/login', 
      name: 'login',
      component: Login,
    },
    //注册
    /*{ 
      path: '/register', 
      name: 'register',
      component: Register,
    },*/
    //其他界面
    { 
      path: '/',
      component: Layout,
      meta:{
        requireAuth:true
      },
      children:[

      ]
    },
  ]
})

export default router
